package leetcode.algorithm.IsSubsequence;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.MethodSource;
import static org.junit.jupiter.api.Assertions.assertEquals;

import java.util.stream.Stream;

public class SolutionTest {

    static Stream<Solution> solutions() {
        return Stream.of(new BasicSolution());
    }

    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithTrue(Solution solution){
        String s = "abc";
        String t = "ahbgdc";
        boolean expected = true;
        assertEquals(expected, solution.isSubsequence(s, t));
    }


    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithFalse(Solution solution){
        String s = "axc";
        String t = "ahbgdc";
        boolean expected = false;
        assertEquals(expected, solution.isSubsequence(s, t));
    }


    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithEmptyString(Solution solution){
        String s = "";
        String t = "ahbgdc";
        boolean expected = true;
        assertEquals(expected, solution.isSubsequence(s, t));
    }

    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithLongT(Solution solution){
        String s = "bcd";
        String t = "";
        boolean expected = true;
        assertEquals(expected, solution.isSubsequence(s, t));
    }
}

