package leetcode.algorithm.IsSubsequence;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.MethodSource;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;
import java.util.stream.Stream;

public class SolutionTest {

    static Stream<Solution> solutions() {
        return Stream.of(new BasicSolution());
    }

    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithTrue(Solution solution) {
        String s = "abc";
        String t = "ahbgdc";
        assertTrue(solution.isSubsequence(s, t));
    }


    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithFalse(Solution solution) {
        String s = "axc";
        String t = "ahbgdc";
        assertFalse(solution.isSubsequence(s, t));
    }


    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithEmptyString(Solution solution) {
        String s = "";
        String t = "ahbgdc";
        assertTrue(solution.isSubsequence(s, t));
    }

    @ParameterizedTest
    @MethodSource("solutions")
    public void testAlgorithmWithLongT(Solution solution) {
        String s = "bcd";
        String t =
                "";
        assertTrue(solution.isSubsequence(s, t));
    }
}

